import React, {useState} from 'react';


function Weather(){
    const [query, setQuery]  = useState('')
    const [weather, setWeather] = useState([])

    const searchWeather = async (e) => {
        e.preventDefault()
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd`

        try {
            const res = await fetch(url)
            const data = await res.json()
            setWeather(data)
            console.log(weather)
            setQuery('')
        } catch(err){
            console.log(err)
        }
    }

    if(weather.cod === 400){
        return(
<h1>Wrong City</h1>
        ) 
    }

    return (
        <>
        <form action="" className="form" onSubmit={searchWeather}>
            <input 
                type="text"
                name="query"
                placeholder='search location'
                className='query'
                value={query}
                onChange={(e) => setQuery(e.target.value) }
            />
            <button>Search Now</button>
        </form>
        </>
    )
}

export default Weather
