import React, { useState } from 'react';
import './App.css'

function App(){
  const apiKey = 'b6ea019473b1df46a1fa1dac301537dd'
  const [query, setQuery] = useState('')
  const [weather, setWeather] = useState([])

    const displayWeather = async (e) => {
        e.preventDefault()
         const url = `https://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=${apiKey}`
         const res =  await fetch(url)
         const data = await res.json()
         setWeather([])
         console.log(data)
    }

    const handleChange = (e) => {
        setQuery(e.target.value)
    }

    return(
      <div className='container'>
          <form className='form' onSubmit={displayWeather}>
            <input 
                type='text'
                name='query'
                placeholder='City or Country'
                className='input_form'
                onChange={handleChange}
                value={query}
            />
          </form>
        <div className='content_container'>
           <h1 className='location'>London, UK</h1>
           <h2 className='temp'>80C</h2>
           <h2 className='description'>Clouds</h2>
        </div>
       </div>
    )
}

export default App
