import React, {useState} from 'react';


function Weather(){

    const [query, setQuery]  = useState('')
    const [weather, setWeather] = useState([])

    const searchWeather = async (e) => {
        e.preventDefault()
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd`

        try {
            const res = await fetch(url)
            const data = await res.json()
            setWeather(data)
            setQuery('')
        } catch(err){
            console.log(err)
        }
    }

  const handleChange = (e) => {
      setQuery(e.target.value)
  }

    return (
        <div className="container">
        <form action="" className="form" onSubmit={searchWeather}>
            <input 
                type="text"
                name="query"
                placeholder='City Or Country'
                className='query'
                value={query}
                onChange={handleChange}
            />
        </form>
        <div>
            <h2>{weather.main}</h2>
        </div>
        </div>
    )
}

export default Weather
