import React, {useState} from 'react';
import SixDaysWeather from './SIxDaysWeather';

function Weather(){
    const [query, setQuery]  = useState('')
    const [sixDays, setSixDays] = useState([])

    const searchWeather = async (e) => {
        e.preventDefault()
        const url = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${query}&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd`

        try {
            const res = await fetch(url)
            const data = await res.json()
            const list = data.list
            const sixDaysList = list.slice(1,7)
            setSixDays(sixDaysList)
            console.log(sixDaysList)
        } catch(err){
            console.log(err)
        }
    }

  const handleChange = (e) => {
      setQuery(e.target.value)
  }

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }

    return (
        <>
        <form action="" className="form" onSubmit={searchWeather}>
            <input 
                type="text"
                name="query"
                placeholder='search location'
                className='query'
                value={query}
                onChange={handleChange}
            />
            <button>Search Now</button>
        </form>
        <div className='list'>
           {sixDays.map(six => (
               <SixDaysWeather six={six} />
           ))}
        </div>
        </>
    )
}

export default Weather
