import React, { Component } from 'react';


class Weather extends Component{
    constructor(){
        super()
        this.state={
            isLoaded:false,
            temperature:[]
        }
    }

   async componentDidMount(){
         const res = await fetch('https://api.openweathermap.org/data/2.5/weather?q=Lagos&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd')
         const response = await res.json()
         const {temp} = response.main 
         this.setState({
             isLoaded:true,
             temperature:temp
         })
    }

     render(){
