import React, { useState } from 'react';
import './App.css'
import Day from './components/Day';

function App(){
  const apiKey = 'b6ea019473b1df46a1fa1dac301537dd'
  const [query, setQuery] = useState('')
  const [weathers, setWeather] = useState([])
  // const [forcast, setForcast]  = useState([])

    const displayWeather = async (e) => {
        e.preventDefault()
         const url = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${query}&units=metric&appid=${apiKey}`
         const res =  await fetch(url)
         const data = await res.json()
         const today = data.list[0]
        console.log(today).temp.day
         setWeather(today)
    }

    const handleChange = (e) => {
        setQuery(e.target.value)
    }

    return(
      <div className='container'>
          <form className='form' onSubmit={displayWeather}>
            <input 
                type='text'
                name='query'
                placeholder='City or Country'
                className='input_form'
                onChange={handleChange}
                value={query}
            />
          </form>
         {typeof weathers.name === 'undefined' ? (
           <div>
             <h3 className='welcome'>Welcome to The Weather App</h3>
           </div>
         ): (

        <div className='content_container'>
           <Day />
           <h1 className='location'>{weathers.name}, {weathers.sys.country} </h1>
           <h2 className='temp'>{Math.floor(weathers.main.temp)}ÂºC</h2>
           <h2 className='description'>{weathers.weather[0].description}</h2>
        </div>
         )
         }

        {weathers.cod === '400' ? (
         <div>
             <h3 className='welcome'>No Location Included</h3>
        </div> 
          ) : 
        (<div></div>) } 
       </div>
    )
}

export default App
