import React, {useState} from 'react';

function Weather(){

    const [sixDays, setSixDays] = useState([])

    const searchWeather = async (e) => {
        e.preventDefault()
        const query = 'london'
        const url = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${query}&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd`

        const res = await fetch(url)
        const data = await res.json()
        const list = data.list
        const sixDaysList = list.slice(1,7)

         setSixDays(sixDaysList)

    }


    return (
        <form action="" className="form" onSubmit={searchWeather}>
            <input 
                type="text"
                name="query"
                placeholder='search location'
                className='query'
            />
            <button>Search Now</button>
        </form>
    )
}

export default Weather
