import React, { Component } from 'react';


class Weather extends Component{
    constructor(){
        super()
        this.state={
            isLoaded:false,
            data:[],
            imgUrl:'',
            location:''
        }
    }

   async componentDidMount(){
         const res = await fetch('https://api.openweathermap.org/data/2.5/weather?q=Lagos&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd')
         const data = await res.json()
         setTimeout(() => {
            this.setState({
                isLoaded:true,
                data:data,
                imgUrl:`http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`
            })
         }, 1000);

    }



     render(){
       const {isLoaded, data, imgUrl} = this.state

        return(
            !isLoaded ? <img className="loading" src='./loading.png' alt=""/>: 
            <div>
                <img src={imgUrl} alt=""/>
                <h2>{Math.floor(data.main.temp)}C</h2>
                <p>({data.weather[0].description})</p>
                <form>
                   <input 
                      type="text"
                      name="location"
                      value={this.state.location}
                      placeholder='insert'
                   />
                </form>
            </div>


        )
     }
}

export default Weather
