import React, {useState} from 'react';

function Weather(){
    const [query, setQuery]  = useState('')
    const [sixDays, setSixDays] = useState([])

    const searchWeather = async (e) => {
        e.preventDefault()
        const url = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${query}&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd`

        try {
            const res = await fetch(url)
            const data = await res.json()
            const list = data.list
            const sixDaysList = list.slice(1,7)
            setSixDays(sixDaysList)
        } catch(err){
            console.log(err)
        }

    }

  const handleChange = (e) => {
      setQuery(e.target.value)
  }

    return (
        <>
        <form action="" className="form" onSubmit={searchWeather}>
            <input 
                type="text"
                name="query"
                placeholder='search location'
                className='query'
                value={query}
                onChange={handleChange}
            />
            <button>Search Now</button>
        </form>
        <div>
           {sixDays.map(six => (
               <div>
                   <img src="http://openweathermap.org/img/wn/${dayIcon[0]}@2x.png" />
                   <h2>{Math.floor(six.temp.day)}</h2>
               </div>
           ))}
        </div>
        </>
    )
}

export default Weather
