import React, { Component } from 'react';


class Weather extends Component{
    constructor(){
        super()
        this.state={
            isLoaded:false,
            data:[],
            imgUrl:''
        }
    }

   async componentDidMount(){
         const res = await fetch('https://api.openweathermap.org/data/2.5/weather?q=Lagos&units=metric&appid=b6ea019473b1df46a1fa1dac301537dd')
         const data = await res.json()
         console.log(data)
         this.setState({
             isLoaded:true,
             data:data,
             imgUrl:`http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`
         })
    }

     render(){
       const {isLoaded, data, imgUrl} = this.state
        return(
            !isLoaded ? <img src={imgUrl} alt=""/> : 
            <div>
                <img src={imgUrl} alt=""/>
                <h2>{data.main.temp}</h2>
                <h3>({data.weather[0].description})</h3>
            </div>
        )
     }
}

export default Weather
